import React, {Fragment, useState} from 'react';
import 'bootstrap/dist/css/bootstrap.min.css'
import './App.css';
import  Header  from "./components/Header";
//import Produtos from "./components/Produtos";
import Button from 'react-bootstrap/Button';
import Card from 'react-bootstrap/Card';
import Form from 'react-bootstrap/Form';
import ImageUploading, { ImageListType } from "react-images-uploading";

type FormElem = React.FormEvent<HTMLFormElement>

interface IProduct{
  name: string
  price: string 
}

export default function App() {

  const[name, setName] = useState<string>("")
  const[price, setPrice] = useState<string>("")
  const[Products, setProducts] = useState<IProduct[]>([])
  const[File, setFile] = React.useState([])



  const handleSubmit = (e: FormElem) => {
    e.preventDefault()
    addProduct(name, price)
    setName("")
    setPrice("")
  }

  const addProduct = (name: string, price: string) => {
    const NewProduct: IProduct[] = [...Products, {name, price}]
    setProducts(NewProduct)
  }

return(
  <Fragment>
     <Header/>
     <Card style={{ width: '18rem' }}>
      <Card.Img variant="top" src="" />
      <Card.Body>
      <Card.Title> Produto </Card.Title>
      <Card.Text>
        <Form onSubmit={handleSubmit}>
          <Form.Group>
          <Form.Label>Insira uma imagem</Form.Label>

          <ImageUploading value={File} onChange={e => setFile(e.target.value)}>
        {({
          imageList,
          onImageUpload,
          onImageRemoveAll,
          onImageUpdate,
          onImageRemove,
          isDragging,
          dragProps
        }) => (
          // write your building UI
          <div className="upload__image-wrapper">
            <button
              style={isDragging ? { color: "red" } : undefined}
              onClick={onImageUpload}
              {...dragProps}
            >
             Insira uma imagem
            </button>
            &nbsp;
            <button onClick={onImageRemoveAll}>Remover</button>
            {SetFile.map((File, index) => (
              <div key={index} className="image-item">
                <img src={File.dataURL} alt="" width="100" />
                <div className="image-item__btn-wrapper">
                  <button onClick={() => onImageUpdate(index)}>Update</button>
                  <button onClick={() => onImageRemove(index)}>Remove</button>
                </div>
              </div>
            ))}
          </div>
        )}
      </ImageUploading>
          </Form.Group>
          <Form.Group className="mb-3" controlId="CadastroProduto">
            <Form.Label>Nome: </Form.Label>
                <Form.Control type="text" placeholder="Ex: macarrão" value={name} onChange={e => setName(e.target.value)}/>
        </Form.Group>
          <Form.Group>
          <Form.Label>Preço: </Form.Label>
          <Form.Control type="text" placeholder="Ex: 6.00" value={price} onChange={e => setPrice(e.target.value)}/>
          </Form.Group>
          <br></br>

          <Button variant="primary" type="submit">
          Cadastrar
          </Button>
        </Form>
        </Card.Text>
        </Card.Body>
      </Card>
      <section>
      {Products.map((product: IProduct, index: number)=> {
          //return (<div key={index}>{product.name}</div>);
          return(<Fragment key={index}> 
          <Card style={{ width: '18rem' }}>
            <Card.Img variant="top" src="" />
            <Card.Body>
            <Card.Title> Produto </Card.Title>
            <Card.Text>
          <Form>
          <Form.Group className="mb-3" controlId="CadastroProduto">
            <Form.Label>Nome: {product.name}</Form.Label>
          </Form.Group> 
          <Form.Group>
          <Form.Label>Preço: {product.price}</Form.Label>
          </Form.Group>

           <Button variant="warning">Editar</Button>{' '}
           <Button variant="danger">Deletar</Button>{' '}
          </Form>
          </Card.Text>
        </Card.Body>
      </Card>
      </Fragment>
    );})} 
      </section>
     </Fragment>);}
