import React, {Fragment, useState} from 'react';
import 'bootstrap/dist/css/bootstrap.min.css'
import './App.css';
import  Header  from "./components/Header";
import Button from 'react-bootstrap/Button';
import Card from 'react-bootstrap/Card';
import Form from 'react-bootstrap/Form';


type FormElem = React.FormEvent<HTMLFormElement>

interface IProduct{
  name: string
  price: string 
  arq: file
}

export default function App() {

  const[name, setName] = useState<string>("")
  const[price, setPrice] = useState<string>("")
  const[Products, setProducts] = useState<IProduct[]>([])
  const[file, setFile] = React.useState<File>()



  const handleSubmit = (e: FormElem) => {
    e.preventDefault()
    aqrT.readAsDataURL(arq);
    console.log("O que é o resultado???", aqrT)
    addProduct(name, price, file)
    setName("")
    setPrice("")
    setFile(aqrT)
  }

  const addProduct = (name: string, price: string, arq: file) => {
    const NewProduct: IProduct[] = [...Products, {name, price, aqr}]
    setProducts(NewProduct)
  }

return(
  <Fragment>
     <Header/>
     <Card style={{ width: '18rem' }}>
      <Card.Img variant="top" src="./imgPadrao.svg" />
      <Card.Body>
      <Card.Title> Produto </Card.Title>
      <Card.Text>
        <Form onSubmit={handleSubmit}>
          <Form.Group>
          <Form.Label>Insira uma imagem</Form.Label>
            <Form.Control type="file" placeholder='Escolha uma imagem' value={aqr} onChange={e => setFile(e.target.value)}/>
        </Form.Group>
          <Form.Group className="mb-3" controlId="CadastroProduto">
            <Form.Label>Nome: </Form.Label>
                <Form.Control type="text" placeholder="Ex: macarrão" value={name} onChange={e => setName(e.target.value)}/>
        </Form.Group>
          <Form.Group>
          <Form.Label>Preço: </Form.Label>
          <Form.Control type="text" placeholder="Ex: 6.00" value={price} onChange={e => setPrice(e.target.value)}/>
          </Form.Group>
          <br></br>

          <Button variant="primary" type="submit">
          Cadastrar
          </Button>
        </Form>
        </Card.Text>
        </Card.Body>
      </Card>
      <section>
      {Products.map((product: IProduct, index: number)=> {
          //return (<div key={index}>{product.name}</div>);
          return(<Fragment key={index}> 
          <Card style={{ width: '18rem' }}>
            <Card.Img variant="top" src={product.file} />
            <Card.Body>
            <Card.Title> Produto </Card.Title>
            <Card.Text>
          <Form>
          <Form.Group className="mb-3" controlId="CadastroProduto">
            <Form.Label>Nome: {product.name}</Form.Label>
          </Form.Group> 
          <Form.Group>
          <Form.Label>Preço: {product.price}</Form.Label>
          </Form.Group>

           <Button variant="warning">Editar</Button>{' '}
           <Button variant="danger">Deletar</Button>{' '}
          </Form>
          </Card.Text>
        </Card.Body>
      </Card>
      </Fragment>
    );})} 
      </section>
     </Fragment>);}
