import React, {Fragment, useState} from 'react';
import 'bootstrap/dist/css/bootstrap.min.css'
import './App.css';
import  Header  from "./components/Header";
//import Produtos from "./components/Produtos";
import Button from 'react-bootstrap/Button';
import Card from 'react-bootstrap/Card';
import Form from 'react-bootstrap/Form';

type FormElem = React.FormEvent<HTMLFormElement>

interface IProduct{
  name: string
  price: number 
}

export default function App() {

  const[name, setName] = useState<string>("")
  const[Products, setProducts] = useState<IProduct[]>([])


  const handleSubmit = (e: FormElem) => {
    e.preventDefault()
    addProduct(name, price)
    setName("")

  }

  const addProduct = (name: string, price: number) => {
    const NewProduct: IProduct[] = [...Products, {name, price}]
    setProducts(NewProduct)
  }

return(
  <Fragment>
     <Header/>
     <Card style={{ width: '18rem' }}>
      <Card.Img variant="top" src="" />
      <Card.Body>
      <Card.Title> Produto </Card.Title>
      <Card.Text>
        <Form onSubmit={handleSubmit}>
          <Form.Group className="mb-3" controlId="CadastroProduto">
            <Form.Label>Nome: </Form.Label>
                <Form.Control type="text" placeholder="Ex: macarrão" value={name} onChange={e => setName(e.target.value) }/>
            <Form.Label>Preço: </Form.Label>
              <Form.Control type="text" placeholder="Ex: 6.00" value={price} onChange={e => setName(e.target.value) }/>
          </Form.Group> 

          <Button variant="primary" type="submit">
          Cadastrar
          </Button>
        </Form>
        </Card.Text>
        </Card.Body>
      </Card>
      <section>
      {Products.map((product: IProduct, index: number)=> {
          //return (<div key={index}>{product.name}</div>);
          return(<Fragment key={index}> 
          <Card style={{ width: '18rem' }}>
            <Card.Img variant="top" src="" />
            <Card.Body>
            <Card.Title> Produto </Card.Title>
            <Card.Text>
          <Form>
          <Form.Group className="mb-3" controlId="CadastroProduto">
            <Form.Label>Nome: {product.name}</Form.Label>
            <Form.Label>Preço: {product.price}</Form.Label>
          </Form.Group> 
            Editar
          </Form>
          </Card.Text>
        </Card.Body>
      </Card>
      </Fragment>
    );})} 
      </section>
     </Fragment>);}
